mapper.insert:
INSERT INTO diary (
    user_id, pet_id, diary_type, diary_title, diary_content, diary_date, diary_images, diary_tags, created_at
) VALUES (
    #{userId}, #{petId}, #{type}, #{title}, #{content}, #{date}, #{images}, #{tags}, NOW()
)

mapper.select:
SELECT * FROM diary 
WHERE user_id = #{userId} 
AND TO_CHAR(diary_date, 'YYYY-MM') = #{yearMonth}

    AND pet_id = #{petId}

ORDER BY diary_date DESC
